var stackTrace = require('stack-trace')

module.exports = function printStack (from, to) {
  var trace = stackTrace.get()
  if (!from) from = 0
  if (!to) to = trace.length - 1
  trace = trace.slice(from + 1, to + 1)
  if (trace.length) {
    trace.forEach(function (t) {
      console.log('  ', t.toString())
    })
  }
}
