
module.exports = function setInterval (fn, millis) {
  var tid

  loop()
  return {
    reset: function () {
      clearTimeout(tid)
      loop()
    },
    clear: function () {
      clearTimeout(tid)
    }
  }

  function loop () {
    tid = setTimeout(function () {
      fn()
      loop()
    }, millis)
  }
}
